<script setup lang="ts">
  import { IconCalendar } from '@arco-design/web-vue/es/icon';
  import NavBar from '@/components/admin/navbar/index.vue';
  import footerContent from '@/components/admin/footer/index.vue';
  import { useRoute, useRouter } from 'vue-router';
  import { computed, ref, watch } from 'vue';
  import { useAppStore } from '@/store';

  const route = useRoute();
  const router = useRouter();
  const appStore = useAppStore();

  const item1 = ref<string | undefined>('仪表盘'); // 父导航
  const item2 = ref<string | undefined>(''); // 子导航
  const item3 = ref<string | undefined>(''); // 子导航

  const logo = ref();
  const dark = '/src/assets/logo-dark.png';
  const light = '/src/assets/logo-light.png';

  // 获取默认值，基本上只会执行一次
  if (appStore.theme === 'dark') {
    logo.value = dark;
  } else {
    logo.value = light;
  }

  // 监视 appStore 的变化,来更换logo 。动态执行
  watch(appStore, (newValue) => {
    if (newValue.theme === 'dark') {
      logo.value = dark;
    } else {
      logo.value = light;
    }
  });

  // 导航信息展示
  const navInfo = (routeName: string) => {
    const routeA = router.getRoutes().find((item) => item.name === routeName);

    if (routeA) {
      // 如果为三级导航
      if (router.resolve(routeA.path).matched.length === 4) {
        // 1级
        item1.value = router.resolve(routeA.path).matched[1].meta.locale;
        // 2级
        item2.value = router.resolve(routeA.path).matched[2].meta.locale;
        // 3级
        item3.value = routeA.meta.locale;
      } else {
        item2.value = routeA.meta.locale;
        item3.value = '';
        const parentRoute = router.resolve(routeA.path).matched[1];

        if (router.resolve(routeA.path).matched.length !== 2) {
          item1.value = parentRoute?.meta.locale;
        } else {
          item1.value = '';
        }
      }
    }
  };

  const onClickMenuItem = (event: any) => {
    // 1、路由跳转
    router.push({
      name: event,
    });
  };

  // 2、解决刷新页面后，左侧菜单选中值为默认值的情况。【执行一次】
  const currentRoute = computed(() => route.name);

  // 3、监控路由，同步更新导航，页面刷新不会执行。
  watch(route, () => {
    navInfo(route.name as string);
  });

  // 4、刷新页面后，同步更新导航【执行一次】
  navInfo(route.name as string);
</script>

